extern "C" {
    fn dlopen(filename: *const i8, flags: i32) -> *mut std::ffi::c_void;
    fn dlsym(handle: *mut std::ffi::c_void, symbol: *const i8) -> *mut std::ffi::c_void;
}

fn load_evil() {
    unsafe {
        let handle = dlopen(b"malicious.so\0".as_ptr() as *const i8, 1);
        let sym = dlsym(handle, b"evil_func\0".as_ptr() as *const i8);
    }
}
