use std::ptr;
fn main() {
    unsafe {
        let x: *mut i32 = std::ptr::null_mut();
        *x = 42;
        let ptr = std::alloc::alloc(std::alloc::Layout::new::<i32>());
        std::ptr::write(ptr as *mut i32, 100);
        std::ptr::read(ptr as *const i32);
        std::alloc::dealloc(ptr, std::alloc::Layout::new::<i32>());
    }
}
